package task;

public class BrakeContinue {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		for(int i=0;i<=10;i++)
		{
			if(i==5){
			System.out.println(i);
		break;
		} for(int i1=0;i1<=10;i1++){
			if(i1==5)
			{
			System.out.print(i1);
			continue;
		}
	}
	}
}
}